# FallSkydive.py# Model of sky diving with air friction  import mathdef FallSkydive(mass = 90, position = 2000, position_open = 1000, DT = 0.001, simLength = 50):    velocity = 0    numIterations = int(simLength/DT) + 1    acceleration_due_to_gravity = -9.81    weight = mass * acceleration_due_to_gravity    projected_area = 28    air_friction = -0.65 * projected_area *  velocity * abs(velocity)    total_force = weight + air_friction    acceleration = total_force / mass#    print "t\tposition\tvelocity"    change_in_velocity = acceleration            change_in_position = velocity    t = 0    timeLst = [0]    positionLst = [position]    speed = abs(velocity)    speedLst = [speed]#    print '%10.2f\t%12.2f\t%12.2f' % (t, position, velocity)    for i in range(1, numIterations):        t = i * DT        timeLst.append(t)	            velocity = velocity + (change_in_velocity) * DT        speed = abs(velocity)        speedLst.append(speed)	            position = position + (change_in_position) * DT        positionLst.append(position)        if (position > position_open):            projected_area = 0.4        else:            projected_area = 28        air_friction = -0.65 * projected_area * velocity * abs(velocity)        total_force = weight + air_friction        acceleration = total_force / mass        change_in_position = velocity        change_in_velocity = acceleration#        print '%10.2f\t%12.2f\t%12.2f' % (t, position, velocity)    outfile = open("fallskydive.dat", 'w')    for i in range(numIterations):        outfile.write("%6.3f\t%6.2f\t%6.2f\n" % (timeLst[i], positionLst[i], speedLst[i]))    outfile.close()FallSkydive()